import { combineReducers } from 'redux'

import {
  ADD_DEFINITION,
  REMOVE_FROM_FAVORITES
} from '../actions'

const initialState = []

function favorites (state = initialState, action) {
  const { definition, word } = action

  switch (action.type) {
    case ADD_DEFINITION:
      state.push({ definition: definition, word: word })
      return {
        state
      }
    case REMOVE_FROM_CALENDAR:
      return {
        ...state,
        [day]: {
          ...state[day],
          [meal]: null
        }
      }
    default:
      return state
  }
}

export default combineReducers({
  food,
  calendar
})
