import { combineReducers } from 'redux'

import {
  ADD_DEFINITION,
  REMOVE_FROM_FAVORITES
} from '../actions'

const initialFavoritesState = {
  definition: null,
  word: null
}

function calendar (state = initialFavoritesState, action) {
  const { day, recipe, meal } = action

  switch (action.type) {
    case ADD_RECIPE:
      return {
        ...state,
        [day]: {
          ...state[day],
          [meal]: recipe.label
        }
      }
    case REMOVE_FROM_CALENDAR:
      return {
        ...state,
        [day]: {
          ...state[day],
          [meal]: null
        }
      }
    default:
      return state
  }
}

export default combineReducers({
  food,
  calendar
})
