import produce from 'immer'
import {
  ADD_DEFINITION,
  REMOVE_FROM_FAVORITES
} from '../actions'

const initialState = { definitions: [] }

function favorites (state = initialState, action) {
  const { definition, word } = action

  switch (action.type) {
    case ADD_DEFINITION:
      return produce(state, draft => {
        draft.definitions.push({
          definition: definition,
          word: word
        })
      })
      break
    case REMOVE_FROM_FAVORITES:
      state.definitions.reduce((result, elem) => {
        return elem.definition.definition !== definition
      }, [])
      break
    default:
      return state
  }
}

export default favorites
